	public static void distinctUrl(List<UrlQueue> list){

		HashMap<String,UrlQueue> distUrlMap = new HashMap<String,UrlQueue>();
		
		for(UrlQueue url:list){
			
			distUrlMap.put(url.getUrl().toLowerCase().trim(), url);
		}
		
		list.clear();
		list.addAll(distUrlMap.values());
		distUrlMap.clear();
		distUrlMap = null;

		
	}

	
	/**
	 * asc2加密
	 * @param val
	 * @param charset
	 * @return
	 */
	public static String encodeAsByte(String val,String charset){
		
		StringBuilder sb = new StringBuilder();
		try{
		    byte[] vbytes = null;
		    if(charset==null||"".equals(charset)){
		    	vbytes = val.getBytes();
		    }else{
		    	vbytes = val.getBytes(charset);
		    }
		    for(byte bt:vbytes){
		    	
		    	if(bt<0){
		    		sb.append("1");
		    		sb.append(String.valueOf(bt).replaceAll("-", "").length());
		    		sb.append(String.valueOf(bt).replaceAll("-", ""));
		    	}else{
		    		sb.append("0");
		    		sb.append(String.valueOf(bt).length());
		    		sb.append(String.valueOf(bt));
		    	}
		    }
		    
		    return sb.toString();
		}catch(Exception ex){
			ex.printStackTrace();
		}
		return val;
	}
	
	/**
	 * asc2解密
	 * @param val
	 * @param charset
	 * @return
	 */
	public static String decodeAsByte(String val,String charset){
		
		StringBuilder sb = new StringBuilder(val);
		byte[] rest = new byte[sb.toString().getBytes().length];
		try{
			int i = 0;
			while(true){
				
				if(sb.length()<=0){break;}
				String tmp = sb.substring(0, 1);
				if("1".equals(tmp)){
					tmp = sb.substring(1, 2);
					tmp = sb.substring(2, 2+Integer.parseInt(tmp));
					rest[i++]= (byte)-Integer.parseInt(tmp);
					sb.delete(0, 2+tmp.length());
				}else{
					tmp = sb.substring(1, 2);
					tmp = sb.substring(2, 2+Integer.parseInt(tmp));
					rest[i++]= (byte)Integer.parseInt(tmp);
					sb.delete(0, 2+tmp.length());
				}
			}
			
		    
		    String res = null;
		    if(charset==null||"".equals(charset)){
		       res = new String(rest);
		    }else{
		    	res = new String(rest,charset);
		    }
		    return res.trim();
		}catch(Exception ex){
			ex.printStackTrace();
		}
		return null;
	}
	
