	public static String filterPrice(String price){
		   
		    return price.split("-")[0].replaceAll("\\s", "").replaceAll("\\.00", "").replaceAll("\\,", "").replaceAll("￥", "");
	}
	
	public static String formatPrice(String price){
		price=price.replaceAll("[,\\s]+", "").replaceAll("[^\\d\\.]", " ").trim();
		if(price.contains(" ")){
			price=price.substring(0,price.indexOf(" "));
		}
		return price;
	}
	
