	 public static String formatDesc(String desc){
		    if(counter(desc,':')>0){
	    		desc = desc.replaceAll(",", " ");
	    	}
		    String strBlack = desc.replaceAll("：", ":").replaceAll("★", " ").replaceAll("\\.\\.\\.\\.|\\.\\.\\.|&amp;|&nbsp;|&#40;|&#41;|&", "")
		                                .replaceAll(":[　 ]+", ":").trim();
		    
		    String[] strBlackKey = strBlack.split("。|；|;|\n|[\\s]{2,}");//\\pZ

		    StringBuilder sb3 = new StringBuilder();
		    for(String line:strBlackKey){
		    	line = line.trim();
		    	if(line.indexOf(":")!=-1){
		    		if(line.startsWith(":")){
		    			sb3.append(line.substring(1).replaceFirst(":", "[xm99]").trim());
		    		}else{
		    			sb3.append(line.replaceFirst(":", "[xm99]").trim());
		    		}
			    	
			    	sb3.append("[xm99]");
		    	}else if((line!=null&&!"".equals(line))){
			    	sb3.append("[xm99]");
			    	sb3.append(line.trim());
			    	sb3.append("[xm99]");
		    	}
		    }
		    return sb3.toString();
	 }
	 
	 
	/**
	 * Remove the zero_price product from the DESC_ORDER_PRICE product list
	 * @param prolist(the DESC_ORDER_PRICE product list)
	 * @return the DESC_ORDER_PRICE product list
	 */
	public static List<Product> removeZeroPriceProduct(List<Product> prolist) {
		// TODO Auto-generated method stub
		
		List<Product> list=prolist;
		for(int i=list.size()-1;i>0;i--){
			if(list.get(i).getMinPrice()!=null){
				if(list.get(i).getMinPrice()>0.0){
					continue;
				}else{
					list.remove(i);
				}				
			}				
		}
		
		return list;
	}
	

	
